#include <iostream>
#include <windows.h>
#include "start&menu.h" 
#include <conio.h>
#include "startgame.h"
#include <vector>
#include <thread>


using namespace std;



struct Question 
{
    string questionText;
    vector<string> options;
    int correctAnswerIndex;
};



void setFullScreen();
void showDifficultyMenu();
void displayCredits();
void setFullScreen();


int main() 
{
    setFullScreen();
    setConsoleFont6(); 
    cout << playerregister();

    _getch(); 

    bool isRunning = true;
    int selectedOption = 0; 

    while (isRunning) {
        
        displayMenu(selectedOption);

        
        char key = _getch();  

        switch (key) {
        case 'w': 
        case 'W':
            selectedOption--;
            if (selectedOption < 0) {
                selectedOption = 4; 
            }
            break;
        case 's': 
        case 'S':
            selectedOption++;
            if (selectedOption > 4) {
                selectedOption = 0; 
            }
            break;
        case '\r':
            switch (selectedOption) {
            case 0:
                showRules();
                break;
            case 1:
                exampleUsage();
                
                break;
            case 2:
                displayCredits();
                break;
            case 3:
                displayAchievements();
                
                break;
            case 4:
                system("cls");
                cout << welcome();
                _getch();
                break;
            default:
                cout << "Invalid option, please try again." << endl;
            }
            break;
        default:
            
            break;
        }
    }

    return 0;
}



